import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useApiService } from './useApiService';
import { ApiError } from '../errors/ApiError';
import type { AddressRequest, CreateUserRequest, UpdateUserRequest, User, Users } from '../models';
import type { UserRole } from '../enums';

const usersKeys = {
  all: ['users'] as const,
  list: (query?: { search?: string; country?: string; role?: string; isActive?: boolean; limit?: number }) => [...usersKeys.all, 'list', query] as const,
  detail: (userId: string) => [...usersKeys.all, 'detail', userId] as const,
};

export { usersKeys };

export function useListUsers(query?: { search?: string; country?: string; role?: string; isActive?: boolean; limit?: number }) {
  const api = useApiService();
  return useQuery({
    queryKey: usersKeys.list(query),
    queryFn: async () => {
      const result = await api.users.listUsers(query);
      if (result.status === 'ok' || result.status === 'created') {
        return result.data;
      }
      throw new ApiError(
        result.response.status,
        result.response.statusText,
        'error' in result ? result.error.message : 'Request failed',
        result.response,
      );
    },
  });
}

export function useCreateUser() {
  const api = useApiService();
  const queryClient = useQueryClient();
  return useMutation({
    mutationFn: async (body: CreateUserRequest) => {
      const result = await api.users.createUser(body);
      if (result.status === 'ok' || result.status === 'created') {
        return result.data;
      }
      throw new ApiError(
        result.response.status,
        result.response.statusText,
        'error' in result ? result.error.message : 'Request failed',
        result.response,
      );
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: usersKeys.all });
    },
  });
}

