namespace Polymorphism;

/// <summary>
/// Extension methods for registering API handlers in the dependency injection container.
/// </summary>
public static class ServiceCollectionEndpointHandlerExtensions
{
    /// <summary>
    /// Registers all API handler implementations from this assembly.
    /// </summary>
    public static IServiceCollection AddApiHandlersFromDomain(this IServiceCollection services)
    {
        services.AddScoped<IListPaymentsHandler, ListPaymentsHandler>();
        services.AddScoped<ICreatePaymentHandler, CreatePaymentHandler>();
        services.AddScoped<IGetPaymentByIdHandler, GetPaymentByIdHandler>();
        services.AddScoped<ISendNotificationHandler, SendNotificationHandler>();
        services.AddScoped<IListNotificationsHandler, ListNotificationsHandler>();

        return services;
    }
}